mapper.sql:
s_hashtag h, sm_ht2post m, s_reply p where h.id in ( SELECT id FROM s_hashtag start with hashtag in
#{aWord}
CONNECT BY PRIOR id = super_id) and h.id = m.hashtag_id and m.post_id = p.id and board_id = #{boardId} and p.descrim = {PostVO.DESCRIM4POST}

mapper.select:

select count(id)
   from s_reply
   where board_id = #{boardId}
      and descrim = #{descrim}


select p.*, w.name w_name, w.descrim w_descrim    
  from (
       SELECT *
       FROM s_reply
      where board_id = #{boardId}
      order by id desc
     OFFSET (#{cri.pageNumber} - 1) * #{cri.amount} ROWS FETCH FIRST #{cri.amount} ROWS ONLY
       ) p, s_party w
 where p.writer_id = w.user_id

mapper.sql:
 from s_reply
where id like #{originalId} || '%'
 and length(id) = #{idLength}

mapper.select:
    select p.*, w.name w_name, w.descrim w_descrim    
     from (   
    		select *
	  	       from s_reply
		    where id like #{originalId} || '%'
		     and length(id) >= #{idLength}
    		order by id
    		) p, s_party w
    where p.writer_id = w.user_id
